import React, { Fragment, useRef } from 'react';

function App() {
  console.log('state render');
  const ref = useRef();

  function onInputChange(e) {
    ref.current.innerText = `Input Text: ${e.target.value}`;
  }

  return (
    <Fragment>
      <Search onInputChange={onInputChange} />
      {ref&&<h1>{ref.current.value}</h1>}
    </Fragment>
  );
}

function Search(props) {
  return <input type="text" onChange={props.onInputChange} />;
}

export default App;
